const menuHamburger=document.querySelector("#navicon"),menuScreen=document.querySelector(".menu");menuHamburger.addEventListener("click",(()=>{menuHamburger.classList.contains("open")?(menuScreen.classList.remove("open"),menuScreen.classList.add("close"),setTimeout((()=>{menuScreen.classList.remove("close"),menuHamburger.classList.remove("open")}),1e3)):(menuHamburger.classList.add("open"),menuScreen.classList.contains("open")||menuScreen.classList.add("open"))}));const logoAnimation=new MagnetMouse({magnet:{element:".header-logo",enabled:!0,distance:7,position:"center"}});logoAnimation.init();const menuAnimation=new MagnetMouse({magnet:{element:".header-menu",enabled:!0,distance:7,position:"center"}});menuAnimation.init();const titleAnimation=new MagnetMouse({magnet:{element:".title-text",enabled:!0,distance:10,position:"center"}});titleAnimation.init();